package com.sirma.javacourse.io.transferobject;

import java.io.ByteArrayInputStream;
import java.io.InputStream;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class ObjectTransfererRunner {

	private static final Logger LOGGER = LoggerFactory.getLogger(ObjectTransfererRunner.class);

	public static void main(String[] args) {

		// 2970 bytes
		String inputs = "";
		InputStream inputStream = new ByteArrayInputStream(inputs.getBytes());

		ObjectTransferer transferer = new ObjectTransferer(inputStream, System.out);

		int transferred = transferer.transfer(123123123, 1);
		LOGGER.info("Number of bytes transferred: {}", transferred); // output is 2969
	}
}
